import React from 'react';
import { TaskCard } from './TaskCard';

export const Tasklist = () => {

    const [tasks, setTasks] = React.useState([
        { id: 1, title: 'Task 1', completed: true },
        { id: 2, title: 'Task 2', completed: true },
        { id: 3, title: 'Task 3', completed: false }
    ]);

    function deleteTask(id) {
        setTasks(tasks.filter((task) => task.id !== id));
    };

    function taskDone(id) {
        setTasks(tasks.map((task) => {
            if (task.id === id) {
                return { ...task, completed: task.completed ? false : true };
            }
            return task;
        }));
    }
    return (
        <>
            <h1>Task List</h1>
            <ul>
                {tasks.map((task) => (
                    <TaskCard key={task.id} task={task} deleteTask={deleteTask} taskDone={taskDone} />
                ))}
            </ul>

            <div className="box success">
                <p className='title'>Task List</p>
                <p className='description'>This is a simple task list application.</p>
            </div>

            <div className="box error">
                <p className='title'>Error</p>
                <p className='description'>An error occurred while fetching tasks.</p>
            </div>
        </>
    )
}
