export const TaskCard = (props) => {
    return (
        <li key={props.task.id} className={props.task.completed ? 'completed' : 'incomplete'}>
            <div className="px-3">
                <span>{props.task.id} - {props.task.title}</span>
                <input
                    className="checkbox-button mx-3"
                    type="checkbox"
                    checked={props.task.completed}
                    onChange={() => props.taskDone(props.task.id)}
                />
            </div>
            <button className="delete" onClick={() => props.deleteTask(props.task.id)}>Delete</button>
        </li>
    )
}
