import React from 'react';
import { TaskCard } from './TaskCard';
import { NotificationBox } from './NotificationBox';
import { AddTaskModal } from './addTaskModal';

export const Tasklist = () => {

    const [tasks, setTasks] = React.useState([
        { id: 1, title: 'Task 1', completed: true },
        { id: 2, title: 'Task 2', completed: true },
        { id: 3, title: 'Task 3', completed: false }
    ]);

    const [isModalOpen, setIsModalOpen] = React.useState(false);

    function deleteTask(id) {
        setTasks(tasks.filter((task) => task.id !== id));
    };

    function taskDone(id) {
        setTasks(tasks.map((task) => {
            if (task.id === id) {
                return { ...task, completed: task.completed ? false : true };
            }
            return task;
        }));
    }

    function addTask(title) {
        const newTask = {
            id: tasks.length + 1, // Simple ID generation
            title: title,
            completed: false
        };
        setTasks([...tasks, newTask]);
    }

    return (
        <>
            <h1>Task List</h1>

            <button 
                className="add-task-button" 
                onClick={() => setIsModalOpen(true)}
            >
                Add New Task
            </button>

            <AddTaskModal 
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onSave={addTask}
            />

            <ul>
                {tasks.map((task) => (
                    <TaskCard key={task.id} task={task} deleteTask={deleteTask} taskDone={taskDone} />
                ))}
            </ul>

            <div className="d-flex">
                <NotificationBox 
                    type="success" 
                    title="Task List" 
                    description="This is a simple task list application."
                />
                <NotificationBox 
                    type="warning" 
                    title="Warning" 
                    description="This is a warning message."
                />
            </div>
        </>
    )
}
