export const ShowTask = () => {
  const tasks = [
    { id: 2011214, title: "Sample Task", time: "02:09:67 AM 29/08/2025" },
    { id: 2011215, title: "Sample Task 2", time: "02:09:67 AM 29/08/2025" },
    { id: 2011216, title: "Sample Task 3", time: "02:09:67 AM 29/08/2025" }
  ];

  return (
    <section className="showTask">
      <div className="head">
        <div>
          <span className="title">Todo</span>
          <span className="count">0</span>
        </div>
        <button className="clearAll">Clear All</button>
      </div>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <p>
              <span className="task">{task.title}</span>
              <span className="time">{task.time}</span>
            </p>
            <i className="bi bi-pencil-square"></i>
            <i className="bi bi-trash"></i>
          </li>
        ))}
      </ul>
    </section>
  )
}
