export const AddTask = ({ tasklist, setTaskList, taskupdate, setTaskUpdate }) => {

  const handleSubmit = (e) => {
    e.preventDefault();

    if (taskupdate && taskupdate.id) {
      const updatedTaskname = e.target.task.value;
      const updatedTasks = tasklist.map((todo) => {
        if(todo.id === taskupdate.id) {
          return { ...todo, title: updatedTaskname };
        }
        return todo;
      });
      setTaskList(updatedTasks);
      setTaskUpdate({});
      e.target.task.value = "";
    } else {
      const randomId = Math.floor(Math.random() * 1000);
      const taskname = e.target.task.value;
      setTaskList([...tasklist, { id: randomId, title: taskname, time: new Date().toLocaleTimeString() + " " + new Date().toLocaleDateString() }]);
      e.target.task.value = "";
    }
  }

  return (
    <section className='addTask'>
      <form onSubmit={handleSubmit}>
        <input type="text" name="task" autoComplete="off" placeholder="Add task" value={taskupdate.title} maxLength="25" />
        <button type="submit">Add Task</button>
      </form>
    </section>
  )
}
