import AddTask from './AddTask';
export const ShowTask = ({ tasklist, setTaskList }) => {

  const deleteTask = (id) => {
    setTaskList(tasklist.filter((task) => task.id !== id));
  }

  const editTask = (id) => {
    const taskname = prompt("Edit task name:");
    if (taskname) {
      setTaskList(
        tasklist.map((task) =>
          task.id === id ? { ...task, title: taskname } : task
        )
      );
    }
  }

  return (
    <section className="showTask">
      <div className="head">
        <div>
          <span className="title">Todo</span>
          <span className="count">{tasklist.length}</span>
        </div>
        <button className="clearAll" onClick={() => setTaskList([])}>Clear All</button>
      </div>
      <ul>
        {tasklist.map((task) => (
          <li key={task.id}>
            <p>
              <span className="task">{task.title}</span>
              <span className="time">{task.time}</span>
            </p>
            <i className="bi bi-pencil-square" onClick={() => editTask(task.id)}></i>
            <i className="bi bi-trash" onClick={() => deleteTask(task.id)}></i>
          </li>
        ))}
      </ul>
    </section>
  )
}
