import React from 'react';

export const AddTask = () => {
  const [tasks, setTasks] = React.useState([]);

  const generateRandomId = () => {
    return Math.floor(1000 + Math.random() * 9000);
  }

  function addTodoTask() {
    const taskInput = document.querySelector('input[name="task"]');
    const taskTitle = taskInput.value;
    const taskTime = new Date().toLocaleTimeString();

    if (taskTitle) {
      setTasks([...tasks, { id: generateRandomId(), title: taskTitle, time: taskTime }]);
      taskInput.value = '';
    }
  }

  return (
    <section className='addTask'>
      <form>
        <input type="text" name="task" autoComplete="off" placeholder="Add task" maxLength="25"/>
        <button type="submit" onClick={addTodoTask}>Add Task</button>
      </form>
    </section>
  )
}
